import * as fbdb from "firebase";
import * as s from "./s";

const c = "";

fbdbApp = null;

export const initApi = () => {
  if(!fbdbApp)
      fbdbApp = fbdb.initializeApp(JSON.parse(s.d(c)));
  return fbdbApp;
}

export const setListener = (endpoint, updaterFn) => {
    initApi();
    fbdb.database().ref(endpoint).on('value', updaterFn);
    return () => fbdb.database().ref(endpoint).off();
}

export const getProjects = (updaterFn) => {
  return setListener('projects', updaterFn)
};

export const pushProject = (endpoint, data) => {
  initApi();
  return fbdb.database().ref(endpoint).push(data);
}
export const pushProjectStatus = (project, index, data) => {
  initApi();
  return fbdb.database().ref("projects/"+index+"/status").set(data);
  //return fbdb.database().ref("projects/"+index+"/status").push(data);
}

export const login = (email, pass) => firebase.auth().signInWithEmailAndPassword(email, pass);
export const logout = () => firebase.auth().signOut();
export const signup = (email, pass) => firebase.auth().createUserWithEmailAndPassword(email, pass);
